mapper.select:
SELECT l.lyear "lyear" ,l.term "term" ,i.lname "lname" ,l.lrcode "lrcode" ,i.lnum "lnum" ,p.opennum "opennum" ,p.PCOMMENT "pcomment" ,l.dividenum "dividenum" ,l.book "book" ,l.datestatus "datastatus" ,(select count(*) from hw_student where register_id=p.register_id) "submitCount" ,( select count(*)from score_mana sm where sm.opennum=p.opennum) "newlimitcount" ,p.REGISTER_DATE "register_date" ,p.PPERIOD "pperiod" ,p.register_id "register_id" ,trunc(p.pperiod)-trunc(sysdate) "hwstatus" ,p.REGISTER_FILE "register_file" ,p.PCOMMENT "pcomment" ,l.pid "pid" FROM lecture l LEFT OUTER JOIN hw_professor p ON p.OPENNUM=l.OPENNUM JOIN lecture_info i ON l.lnum=i.LNUM JOIN lecture_room lr ON l.lrcode=lr.lrcode WHERE l.pid=#{pid} and not register_id IS null
and lyear=2021
and lyear=#{lyear}
and term=#{term}
and l.lnum=#{lnum}
ORDER BY p.register_date desc

mapper.update:
UPDATE  hw_student 
   SET  score=#{score} 
 WHERE  submit_sid=#{submitSid}
   AND  register_id=#{registerId}

        UPDATE	score_mana
	  	   SET  homework=autoscore(#{submitSid},#{opennum}) 
	     WHERE  sid=#{submitSid} 
	       AND opennum=#{opennum}

UPDATE	hw_professor
   SET  pperiod=#{pperiod}
   	   ,pcomment=#{pcomment}
   	   ,register_file=#{registerFile}
 WHERE register_id=#{registerId}
