mapper.select:
SELECT M.*, L.LYEAR, L.TERM, I.LNAME, I.CREDIT, FIND_CODE_NAME(100, I.DCODE) DIVISION, P.PNAME FROM SCORE_MANA M, STUDENTS S, LECTURE L, LECTURE_INFO I, PROFESSOR P WHERE M.SID = S.SID AND M.OPENNUM = L.OPENNUM AND L.LNUM = I.LNUM AND S.SID = #{sid} AND p.pid = I.pid
AND L.lyear = #{lyear} AND L.term = #{term}
ORDER BY L.LYEAR, L.TERM

SELECT I.LNUM, I.LNAME, FIND_CODE_NAME(100, I.DCODE) DIVISION, L.DIVIDENUM,
        I.GRADE TARGET, I.CREDIT,
        P.PNAME, L.TIMETABLE, R.LRNAME
FROM LECTURE L, LECTURE_INFO I, LECTURE_ROOM R, PROFESSOR P
WHERE P.PID = L.PID
AND L.PID = I.PID
AND L.LRCODE = R.LRCODE
AND L.LYEAR = #{lyear}
AND L.TERM = #{term}
AND I.DCODE = #{dcode}
AND R.LOCATION = #{location}
AND L.LNUM = I.LNUM

SELECT SNAME, PWD
FROM STUDENTS
WHERE SID = #{sid}
AND PWD = #{pwd}

mapper.update:
UPDATE STUDENTS
SET PWD = #{pwd}
WHERE SID = #{sid}

mapper.select:
SELECT *
FROM students
WHERE sid = #{sid} AND email = #{email}
