mapper.select:
SELECT I.LNUM, I.LNAME, FIND_CODE_NAME(100, I.DCODE) DIVISION,
        I.GRADE TARGET, I.CREDIT,
        P.PNAME, L.TIMETABLE, R.LRNAME
FROM LECTURE L, LECTURE_INFO I, LECTURE_ROOM R, PROFESSOR P
WHERE P.PID = L.PID
AND L.PID = I.PID
AND L.LRCODE = R.LRCODE
AND L.LYEAR = #{lyear}
AND L.TERM = #{term}
AND I.DCODE = #{dcode}
AND R.LOCATION = #{location}
AND L.LNUM = I.LNUM

SELECT PNAME, EMAIL, PPHONE
FROM PROFESSOR
WHERE PNAME = #{pname}

SELECT SNAME, PWD
FROM STUDENTS
WHERE SID = #{sid}
AND PWD = #{pwd}

mapper.update:
UPDATE STUDENTS
SET PWD = #{pwd}
WHERE SID = #{sid}
