function check() {
  const email = $("#email-for-pass").val();
  const sid = $("#sid-for-pass").val();

  if (email == null || email === undefined || email == "") {
    alert("학번을 입력해주세요");
    $("#sid-for-pass").focus();
  } else if (sid == null || sid === undefined || sid == "") {
    alert("이메일을 입력해주세요");
    $("#email-for-pass").focus();
  } else {
    $.ajax({
      url: "forgetChkEmail",
      type: "POST",
      dataType: "json",
      data: {
        email: email,
        sid: sid,
      },
      success: function (result) {
        console.log(result);
        $("#sname").val(result.sname);
        $("#forgetPwdAuth").submit();
      },
      error: function (err) {
        console.log(err);
        alert("존재하지 않는 학번 또는 이메일 입니다.");
      },
    });
  }
}

function authCheck(num) {
  const authNum = $("#authNum").val();

  if (num == authNum) {
    $("#forgetPwdNew").submit();
  } else {
    alert("인증번호가 틀립니다.");
    $("#authNum").focus();
  }
}
